mapper.sql:
where  1=1 
	  
	 and id=#{id} 
	
	  
	 and cpId=#{cpId} 
	
	  
	 and myepayAppId=#{myepayAppId} 
	
	  
	 and cpCode=#{cpCode} 
	
	  
	 and appName like '%${appName}%'
	
	  
	 and wxAppId=#{wxAppId}
	
	  
	  and wxAppSecret=#{wxAppSecret}
	
	  
	 and appSeq=#{appSeq}
	
	  
	 and appType=#{appType} 
	
	  
	 and descript=#{descript} 
	
	  
	 and myepaySdkVer=#{myepaySdkVer}  	 
	
	  
	 and talkingdataAppid=#{talkingdataAppid} 
	
	  
	 and talkingdataKey=#{talkingdataKey} 
	
	  
	 and myepayChanAppId=#{myepayChanAppId} 
	
	  
	 and status=#{status} 
	
	
	  
	 and appSort=#{appSort} 
	
	  
	 and createPerson=#{createPerson} 
	
	  
	 and createTime=#{createTime} 
	
	  
	 and lastUpdatePerson=#{lastUpdatePerson} 
	
	  
	 and lastUpdateTime=#{lastUpdateTime} 
	
	  
	 and appRankSeq=#{appRankSeq}

mapper.insert:
SELECT UUID_SHORT() as id;
insert into ( ) values( #{id}, #{cpId}, #{cpCode}, #{appName}, #{wxAppId}, #{wxAppSecret}, #{appSeq}, #{appType}, #{myepayAppId}, #{descript}, #{myepaySdkVer}, #{myepayChanAppId}, #{status},
#{appSort}, #{createPerson}, #{createTime}, #{lastUpdatePerson}, #{lastUpdateTime}, #{appRankSeq}, #{ talkingdataAppid}, #{talkingdataKey} )
